<# : batch portion
@echo off
setlocal DisableDelayedExpansion
echo "%*"|find /i "-el">nul && set _elev=1
set _PSarg="""%~f0""" -el
setlocal EnableDelayedExpansion
>nul 2>&1 fltmc || >nul 2>&1 net session || (
    if not defined _elev (
        powershell -NoProfile -Command "Start-Process cmd.exe -ArgumentList '/c', '!_PSarg!' -Verb RunAs" && exit /b 0
        exit /b 1
    )
)
where pwsh.exe>nul 2>&1 && set "PS1=pwsh" || set "PS1=powershell"
Color 0F
%PS1% -nop -c "Get-Content '%~f0' -Raw | iex"
goto :eof
: end batch / begin powershell #>

$Host.UI.RawUI.WindowTitle = 'Microsoft Edge WebView2 Runtime'
Clear-Host
$ProgressPreference = 'SilentlyContinue'
function Get-FileFromWeb {
	param([string]$URL, [string]$File)
 	try{if(Get-Command curl.exe -ErrorAction SilentlyContinue){curl.exe -L $URL -o $File -s}
 	elseif((Get-Service BITS -ErrorAction SilentlyContinue).Status -eq 'Running'){
 	Start-BitsTransfer -Source $URL -Destination $File -ErrorAction Stop
 	}else{Invoke-WebRequest -Uri $URL -OutFile $File -UseBasicParsing -ErrorAction Stop}
	}catch{throw $_}
}

Write-Host "Installing Microsoft Edge WebView2 Runtime..."
try {
    Get-FileFromWeb "https://go.microsoft.com/fwlink/p/?LinkId=2124703" "$env:TEMP\MicrosoftEdgeWebview2Setup.exe"
    Start-Process -Wait "$env:TEMP\MicrosoftEdgeWebview2Setup.exe" -ErrorAction Stop
}
catch {
    try {
        Write-Host "Second method failed, trying third..." -ForegroundColor Yellow
        Get-FileFromWeb "https://msedge.sf.dl.delivery.mp.microsoft.com/filestreamingservice/files/304fddef-b073-4e0a-b1ff-c2ea02584017/MicrosoftEdgeWebview2Setup.exe" "$env:TEMP\EdgeWebView.exe"
        Start-Process -Wait "$env:TEMP\EdgeWebView.exe" -ErrorAction Stop
    }
    catch {
        try {
        	Write-Host "First method failed, trying second..." -ForegroundColor Yellow
        	Get-FileFromWeb "https://go.microsoft.com/fwlink/?linkid=2124701" "$env:TEMP\MicrosoftEdgeWebView2RuntimeInstallerX64.exe"
        	Start-Process -Wait "$env:TEMP\MicrosoftEdgeWebView2RuntimeInstallerX64.exe" -ErrorAction Stop
        }
        catch {
    		Write-Host "$($_.Exception.Message)" -ForegroundColor Red
    		$null = $Host.UI.RawUI.ReadKey("NoEcho,IncludeKeyDown")
    		exit 1
        }
    }
}

# Disable Edge scheduled tasks
Get-ScheduledTask | Where-Object { $_.TaskName -like "*Edge*" } | ForEach-Object { Disable-ScheduledTask -TaskName $_.TaskName | Out-Null }

# Disable Edge WebView and WebView2 updates
# Remove "Win32 Web View Host" / "Desktop App Web Viewer" app
$bat = @'
@echo off & setlocal DisableDelayedExpansion
:: https://privacy.sexy — v0.13.8 — Mon, 24 Nov 2025 16:33:05 GMT
where PowerShell >nul 2>&1 || (
    echo PowerShell is not available. Please install or enable PowerShell.
    pause & exit 1
)
:: Ensure admin privileges
echo "%*" | find /i "-el" >nul && set _elev=1
set _PSarg="""%~f0""" -el
setlocal EnableDelayedExpansion
>nul 2>&1 fltmc || (
    if not defined _elev powershell -NoProfile "start cmd.exe -arg '/c \"!_PSarg:'=''!\"' -verb runas" && exit /b 0
    echo Right-click on the script and select "Run as administrator".
    pause & exit 1
)
:: Initialize environment
setlocal EnableExtensions DisableDelayedExpansion


:: ----------------------------------------------------------
:: --------Disable Edge WebView and WebView2 updates---------
:: ----------------------------------------------------------
echo --- Disable Edge WebView and WebView2 updates
:: Set the registry value: "HKLM\SOFTWARE\Policies\Microsoft\EdgeUpdate!Update{F3017226-FE2A-4295-8BDF-00C3A9A7E4C5}"
PowerShell -ExecutionPolicy Unrestricted -Command "$registryPath = 'HKLM\SOFTWARE\Policies\Microsoft\EdgeUpdate'; $data =  '0'; reg add 'HKLM\SOFTWARE\Policies\Microsoft\EdgeUpdate' /v 'Update{F3017226-FE2A-4295-8BDF-00C3A9A7E4C5}' /t 'REG_DWORD' /d "^""$data"^"" /f"
:: ----------------------------------------------------------


