function Uninstall-RemoteDesktop {
 	Write-Output "Uninstalling Remote Desktop Connection..."
	# Start mstsc.exe with bogus switch (triggers "fake uninstall" UI)
	$p = Start-Process "mstsc.exe" -ArgumentList "/Uninstall" -WindowStyle Hidden -PassThru

	# Wait up to X seconds for it to close on its own, else kill it
	$timeoutSec = 5
	if (-not $p.WaitForExit($timeoutSec * 1000)) {
	    try { $p.Kill() } catch {}
	}

	# Extra safety: kill any stragglers with suspicious titles
	$killTitles = 'Restart','Restart Required','Remote Desktop Connection'
	Get-Process -ErrorAction SilentlyContinue | Where-Object {
	    $killTitles | ForEach-Object { $_match = $_; if ($_.MainWindowTitle -like "*$_match*") { $true } }
	} | ForEach-Object { Stop-Process $_.Id -Force }

	# Remove Start Menu/Desktop shortcuts
	$names  = "Remote Desktop Connection.lnk","mstsc.lnk"
	$paths  = @(
	    "$env:ProgramData\Microsoft\Windows\Start Menu\Programs\Accessories",
	    "$env:AppData\Microsoft\Windows\Start Menu\Programs\Accessories",
	    "$env:ProgramData\Microsoft\Windows\Start Menu\Programs",
	    "$env:AppData\Microsoft\Windows\Start Menu\Programs",
	    "$env:Public\Desktop",
	    "$env:UserProfile\Desktop"
	)
	foreach ($p in $paths) {
	    foreach ($n in $names) {
	        $f = Join-Path $p $n
	        if (Test-Path $f) { Remove-Item $f -Force }
	    }
	}

	# Remove "Uninstall" registry entries mentioning Remote Desktop
	$regPaths = @(
	    "HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\Uninstall",
	    "HKLM:\SOFTWARE\WOW6432Node\Microsoft\Windows\CurrentVersion\Uninstall"
	)
	foreach ($rp in $regPaths) {
	    Get-ChildItem $rp -ErrorAction SilentlyContinue | ForEach-Object {
	        $props = Get-ItemProperty $_.PSPath -ErrorAction SilentlyContinue
	        if ($props.DisplayName -like "*Remote Desktop*") {
	            Remove-Item $_.PSPath -Recurse -Force
	        }
	    }
	}
}

