	reg add "HKCU\Software\Microsoft\Windows\DWM" /v "ColorizationAfterglow" /t REG_DWORD /d "3305093496" /f | Out-Null
	
	reg add "HKU\.DEFAULT\Software\Microsoft\Windows\CurrentVersion\Explorer\Accent" /v "StartColorMenu" /t REG_DWORD /d "4284394495" /f | Out-Null
	reg add "HKU\.DEFAULT\Software\Microsoft\Windows\CurrentVersion\Explorer\Accent" /v "AccentColorMenu" /t REG_DWORD /d "4286102015" /f | Out-Null
	reg add "HKU\.DEFAULT\Software\Microsoft\Windows\CurrentVersion\Explorer\Accent" /v "AccentPalette" /t REG_BINARY /d "FFE5CEFFFFD3ABFFFEC691FFFFB978FFFFAB5EFFFF9E44FFFE8C21FF88179800" /f | Out-Null
	
	reg add "HKU\.DEFAULT\Software\Microsoft\Windows\DWM" /v "AccentColor" /t REG_DWORD /d "4286102015" /f | Out-Null
	reg add "HKU\.DEFAULT\Software\Microsoft\Windows\DWM" /v "ColorizationColor" /t REG_DWORD /d "3305093496" /f | Out-Null
	reg add "HKU\.DEFAULT\Software\Microsoft\Windows\DWM" /v "ColorizationAfterglow" /t REG_DWORD /d "3305093496" /f | Out-Null

	# StartAllBack ROG Orb (W11)
	$Dest="C:\Program Files\StartAllBack\Orbs\rog.png"
	if (!(Test-Path (Split-Path $Dest))) { New-Item -Path (Split-Path $Dest) -ItemType Directory -Force | Out-Null }
	iwr "https://github.com/ManueITest/Windows/raw/main/rog.png" -OutFile $Dest	
	New-Item -Path "HKCU:\Software\StartIsBack" -Force | Out-Null
	Set-ItemProperty -Path "HKCU:\Software\StartIsBack" -Name "OrbBitmap" -Value "C:\Program Files\StartAllBack\Orbs\rog.png"
	
	# ACTIVATE CUSTOM CURSOR
 	Get-FileFromWeB -URL "https://github.com/ManueITest/Windows/raw/refs/heads/main/Cursor.zip" -File "$env:TEMP\Cursor.zip"
	Expand-Archive "$env:TEMP\Cursor.zip" -Dest "$env:TEMP" -Force
	
	$inf = Join-Path $env:TEMP "install.inf"
	if (!(Test-Path $inf)) {}
	
	$p = Start-Process rundll32 "setupapi.dll,InstallHinfSection DefaultInstall 132 $inf" -WindowStyle Hidden -PassThru
	
	Add-Type 'using System;using System.Runtime.InteropServices;public class C{[DllImport("user32.dll")]public static extern bool SystemParametersInfo(uint a,uint b,IntPtr c,uint d);public static void R(){SystemParametersInfo(0x57,0,IntPtr.Zero,0);}}' | Out-Null
	[C]::R()
	Start-Sleep -Milliseconds 300
	
	1..3 | ForEach-Object {
	    Get-Process rundll32 | Where-Object { $_.MainWindowTitle -match "Mouse|Cursors|Pointer" } | ForEach-Object {
	        $_.CloseMainWindow() | Out-Null
	        Start-Sleep -Milliseconds 200
	        if (-not $_.HasExited) { Stop-Process $_ -Force }
	    }
	
	    if ($p -and -not $p.HasExited) {
	        $p.CloseMainWindow() | Out-Null
	        if (-not $p.WaitForExit(500)) { Stop-Process $p -Force }
	    }
	
	    Get-Process rundll32 | Where-Object { $_.Id -ne $PID } | Stop-Process -Force
	}

 	# SET CUSTOM WALLPAPER
  	# Define paths
	$picturesFolder = [Environment]::GetFolderPath("MyPictures")
	$persistentWallpaperPath = Join-Path $picturesFolder "CustomWallpaper.jpg"
	
	# Download wallpaper to Pictures folder
	$url = "https://github.com/ManueITest/Windows/raw/refs/heads/main/Wallpaper.jpg"
	Get-FileFromWeb $url $persistentWallpaperPath
	
	# Set wallpaper using SystemParametersInfo
	try {
	    Add-Type @"
	    using System.Runtime.InteropServices;
	    public class Wallpaper {
	        public const int SPI_SETDESKWALLPAPER = 0x0014;
	        public const int SPIF_UPDATEINIFILE = 0x01;
	        public const int SPIF_SENDWININICHANGE = 0x02;
	        [DllImport("user32.dll", CharSet=CharSet.Auto)]
	        public static extern int SystemParametersInfo(int uAction, int uParam, string lpvParam, int fuWinIni);
	    }
"@
	    Set-ItemProperty "HKCU:\Control Panel\Desktop" -Name "WallpaperStyle" -Value "10"
	    Set-ItemProperty "HKCU:\Control Panel\Desktop" -Name "TileWallpaper" -Value "0"
	
	    [Wallpaper]::SystemParametersInfo(0x0014, 0, $persistentWallpaperPath, 1 -bor 2) | Out-Null
	} catch {}

	if ((Get-ItemProperty 'HKLM:\SOFTWARE\Microsoft\Windows NT\CurrentVersion').CurrentBuild -ge 22000) {
		# DisableRoundCorners
		iwr "https://github.com/valinet/Win11DisableRoundedCorners/releases/latest/download/Win11DisableOrRestoreRoundedCorners.exe" -OutFile "$env:TEMP\Win11DisableOrRestoreRoundedCorners.exe"
		Start-Process "$env:TEMP\Win11DisableOrRestoreRoundedCorners.exe"
	} else { $null }
	
	# Restart Explorer to apply changes
	Start-Process explorer
}

