# DISABLE TELEMETRY
if ($DisableTelemetry) {
    Write-Host "Disabling Telemetry..." -ForegroundColor Green
 	# CTT WinUtil Tweaks
	$json = @'
{
    "WPFTweaks":  [
                      "WPFTweaksWifi",
                      "WPFTweaksRazerBlock",
                      "WPFTweaksDisableWpbtExecution",
                      "WPFTweaksDisableLMS1",
                      "WPFTweaksStorage",
                      "WPFTweaksConsumerFeatures",
                      "WPFTweaksTele",
                      "WPFTweaksAH",
                      "WPFTweaksBlockAdobeNet",
                      "WPFTweaksLoc",
                      "WPFTweaksPowershell7Tele",
                      "WPFTweaksRecallOff",
                      "WPFTweaksDisableCrossDeviceResume"
                   ]
}
'@
	$config = "$env:TEMP\tweaks.json"
	$script = "$env:TEMP\winutil.ps1"
	
	Set-Content -Path $config -Value $json -Encoding ASCII
	iwr "https://github.com/ChrisTitusTech/winutil/releases/latest/download/winutil.ps1" -OutFile $script
	
	# Use Start-Process with redirected output
	$outFile = "$env:TEMP\winutil_log.txt"
	$psi = New-Object System.Diagnostics.ProcessStartInfo
	$psi.FileName = "powershell.exe"
	$psi.Arguments = "-NoProfile -Sta -ExecutionPolicy Bypass -File `"$script`" -Config `"$config`" -Run"
	$psi.RedirectStandardOutput = $true
	$psi.RedirectStandardError  = $true
	$psi.UseShellExecute = $false
	$psi.CreateNoWindow = $true
	
	$p = New-Object System.Diagnostics.Process
	$p.StartInfo = $psi
	$p.Start() | Out-Null
	
	$reader = $p.StandardOutput
	while (-not $p.HasExited) {
	    $line = $reader.ReadLine()
	    if ($line -ne $null) {
	        Write-Host $line
	        if ($line -match "Tweaks are Finished") {
	            $p.Kill()
	            break
	        }
	    } else {
	    }
	}

	# Win11Debloat Tweaks
	& ([scriptblock]::Create((irm "https://debloat.raphi.re/"))) `
	    -Silent `
	    -DisableStartRecommended `
	    -DisableTelemetry `
	    -DisableSuggestions `
	    -DisableEdgeAds `
	    -DisableDesktopSpotlight `
	    -DisableLockscreenTips `
	    -DisableSettings365Ads `
	    -DisableBing `
	    -DisableRecall `
	    -DisableEdgeAI `
	    -DisablePaintAI `
	    -DisableNotepadAI `
	    -DisableFastStartup `
	    -DisableModernStandbyNetworking `
	    -HideChat `
	    -DisableWidgets `
	 
	Run-WPD
 
    Run-ShutUp10

    Run-PrivacySexy
}

