	Write-Host "Disabling Security..." -ForegroundColor Green
	Set-PasswordNeverExpires
	Disable-Defender
 	Disable-Mitigations
	Write-Output "Disabling UAC..."
	# Disable UAC
	Set-ItemProperty -Path "HKLM:\Software\Microsoft\Windows\CurrentVersion\Policies\System" -Name "EnableLUA" -Value 0 -Type DWord | Out-Null
}

# Enable SECURITY
if ($EnableSecurity) {
	Write-Host "Enabling Security..." -ForegroundColor Green

	Enable-Defender
 	Default-Mitigations
	Write-Output "Enabling UAC..."
	# Enable UAC
	Set-ItemProperty -Path "HKLM:\Software\Microsoft\Windows\CurrentVersion\Policies\System" -Name "EnableLUA" -Value 1 -Type DWord | Out-Null
}

